INSERT INTO `wp_comments` VALUES ('1', '1', 'Một người bình luận WordPress', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2017-08-08 09:46:09', '2017-08-08 09:46:09', 'Xin chào, đây là một bình luận\nĐể bắt đầu với quản trị bình luận, chỉnh sửa hoặc xóa bình luận, vui lòng truy cập vào khu vực Bình luận trong trang quản trị.\nAvatar của người bình luận sử dụng <a href=\"https://gravatar.com\">Gravatar</a>.', '0', '1', '', '', '0', '0');
INSERT INTO `wp_comments` VALUES ('2', '83', 'WooCommerce', 'woocommerce@wpshop.localhost', '', '', '2017-08-10 08:56:43', '2017-08-10 08:56:43', 'Thực hiện thanh toán khi giao hàng Trạng thái đơn hàng đã được chuyển từ Pending payment sang Đang xử lý.', '0', '1', 'WooCommerce', 'order_note', '0', '0');
INSERT INTO `wp_comments` VALUES ('3', '90', 'WooCommerce', 'woocommerce@wpshop.localhost', '', '', '2017-08-10 09:09:47', '2017-08-10 09:09:47', 'Chờ thanh toán chuyển khoản Trạng thái đơn hàng đã được chuyển từ Pending payment sang On hold.', '0', '1', 'WooCommerce', 'order_note', '0', '0');
